function initHeightFix() {
	$(window).on('resize',function() {
		var topBar = $('.video-title');
		var bottomBar = $('.bottom-bar');
		var video = $('.live-video');
		var chatList = $('.chat-list');

		var topBarHeight = topBar.innerHeight();
		var bottomBarHeight = bottomBar.innerHeight();
		var commonHeight = video.innerHeight();
		var leftSidebar = $('.video-sidebar-scroll');

		chatList.css('max-height', commonHeight - topBarHeight);
		leftSidebar.css('max-height', commonHeight + topBarHeight);


		var checkWidth = $(window).width();
		var checkHeight = $(window).height();

		var liveActionHeight = $('.live-action-bar').outerHeight();
		var headerHeight = $('#header').outerHeight();
		var chatBlock = $('.chat-block');

		// console.log(liveActionHeight);

		if (checkWidth < 768) {
			chatBlock.css('height', checkHeight - commonHeight - liveActionHeight - headerHeight);
			// console.log(checkHeight - commonHeight);
			var chatBlockHeight = chatBlock.outerHeight();
			var chatFooterHeight = $('.chat-footer').outerHeight();
			var chatHeaderHeight = $('.chat-header').outerHeight();

			// console.log(chatBlockHeight);

			chatList.css('max-height', chatBlockHeight - chatFooterHeight - chatHeaderHeight);
		} else if (checkWidth >= 768) {
			chatBlock.css('height', 'auto');
		}

	}).trigger('resize');
}

