function initLoginSection() {
	var loginLinks = jQuery('.login-email > a');
	var registerLinks = jQuery('.login-form .alternate > a');
	var memberLinks = jQuery('.signup-form .alternate > a');
	
	loginLinks.on('click', function(e){
		e.preventDefault();
		var loginLink = jQuery(this);
		loginLink.parents('.sign-info').hide();
		loginLink.parents('.sign-info').siblings('.login-form').show();
	});
		

	registerLinks.on('click', function(f){
		f.preventDefault();
		var registerLink = jQuery(this);
		registerLink.parents('.login-form').hide();
		registerLink.parents('.login-form').siblings('.signup-form').show();
	});

	memberLinks.on('click', function(e){
		e.preventDefault();
		var memberLink = jQuery(this);
		memberLink.parents('.signup-form').hide();
		memberLink.parents('.signup-form').siblings('.login-form').show();
	});
}

