// initialize fixed blocks on scroll
function initStickyScrollBlock() {
	jQuery('.timeline-sidebar').stickyScrollBlock({
		setBoxHeight: false,
		activeClass: 'fixed-position',
		positionType: 'fixed',
		extraTop: function() {
			var totalHeight = 0;
			jQuery('0').each(function() {
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		}
	});
	jQuery('.live-action-bar').stickyScrollBlock({
		setBoxHeight: false,
		activeClass: 'fixed-position',
		container: '.video-row',
		positionType: 'fixed',
		extraTop: function() {
			var totalHeight = 0;
			jQuery('0').each(function() {
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		}
	});
}

